  const month = props.date.toLocaleString("hr-eU", { month: "long" });
  const year = props.date.toLocaleString("hr-eU", { year: "numeric" });
  const day = props.date.toLocaleString("hr-eU", { day: "numeric" });

  return (
    <div className="expense-item">
      <div>
        <div>{}</div>
        <div>{}</div>
        <div>{}</div>
      </div>
      <div className="expense-item__description">
        <h2>{props.title}</h2>
        <div className="expense-item__price">$ {props.amount}</div>
      </div>
    </div>
  );
} //podatke iz objekata koje smo kreirali u app.js zatim povezujemo unutar naše komponente
